-- ----------------------------
-- View structure for vw_select_goods
-- ----------------------------
DROP VIEW IF EXISTS `vw_select_goods`;
CREATE ALGORITHM=UNDEFINED DEFINER=`root`@`%` SQL SECURITY DEFINER VIEW `vw_select_goods` AS select `a`.`id` AS `obj_id`,`a`.`deco_id` AS `deco_id`,`a`.`room_id` AS `room_id`,`a`.`amount` AS `amount`,`b`.`name` AS `room_name`,`c`.`id` AS `id`,`c`.`title` AS `title`,`c`.`coverimg` AS `coverimg`,`c`.`brand_id` AS `brand_id`,`c`.`cat_id` AS `cat_id`,`c`.`markup` AS `markup`,`c`.`content` AS `content`,`c`.`sort` AS `sort`,`c`.`unit` AS `unit`,`c`.`price` AS `price`,`c`.`markup_price` AS `markup_price`,`d`.`title` AS `cat_title`,`e`.`name` AS `brand_name` from ((((`gms_goodsdeco` `a` join `gms_room` `b` on((`a`.`room_id` = `b`.`id`))) join `gms_goods` `c` on((`a`.`goods_id` = `c`.`id`))) join `gms_category` `d` on((`c`.`cat_id` = `d`.`id`))) join `gms_brand` `e` on((`c`.`brand_id` = `e`.`id`))) ;
