INSERT INTO `gms_article` VALUES ('136', '105', '马卡龙色的家，你喜欢吗？', '&lt;div&gt;\r\n	&lt;br /&gt;\r\n&lt;/div&gt;\r\n&lt;div&gt;\r\n	&lt;blockquote style=&quot;font-family:微软雅黑;&quot;&gt;\r\n		&lt;p style=&quot;text-align:center;&quot;&gt;\r\n			&lt;span style=&quot;color:#000000;font-family:宋体, SimSun;font-size:14px;&quot;&gt;&lt;span style=&quot;line-height:25.6px;&quot;&gt;马卡龙，&lt;/span&gt;&lt;span style=&quot;line-height:25.6px;&quot;&gt;&lt;/span&gt;&lt;/span&gt; \r\n		&lt;/p&gt;\r\n		&lt;p style=&quot;text-align:center;&quot;&gt;\r\n			&lt;span style=&quot;color:#000000;font-family:宋体, SimSun;font-size:14px;&quot;&gt;这种幸福而又甜蜜的颜色，&lt;/span&gt; \r\n		&lt;/p&gt;\r\n		&lt;p style=&quot;text-align:center;&quot;&gt;\r\n			&lt;span style=&quot;color:#000000;font-family:宋体, SimSun;font-size:14px;&quot;&gt;当它融入家装中，&lt;/span&gt; \r\n		&lt;/p&gt;\r\n		&lt;p style=&quot;text-align:center;&quot;&gt;\r\n			&lt;span style=&quot;color:#000000;font-family:宋体, SimSun;font-size:14px;&quot;&gt;又会产生怎样的效果呢？&lt;/span&gt; \r\n		&lt;/p&gt;\r\n	&lt;/blockquote&gt;\r\n	&lt;p style=&quot;color:#3E3E3E;font-family:-apple-system-font, \'Helvetica Neue\', \'PingFang SC\', \'Hiragino Sans GB\', \'Microsoft YaHei\', sans-serif;font-size:16px;background-color:#FFFFFF;&quot;&gt;\r\n		&lt;span style=&quot;font-family:宋体, SimSun;font-size:14px;&quot;&gt;&lt;br /&gt;\r\n&lt;/span&gt; \r\n	&lt;/p&gt;\r\n	&lt;p style=&quot;color:#3E3E3E;font-family:-apple-system-font, \'Helvetica Neue\', \'PingFang SC\', \'Hiragino Sans GB\', \'Microsoft YaHei\', sans-serif;font-size:16px;background-color:#FFFFFF;&quot;&gt;\r\n		&lt;span style=&quot;font-family:宋体, SimSun;font-size:14px;&quot;&gt;▼整个客厅都弥漫着一股小清新的味道，连小编见了也忍不住少女心爆棚。天蓝色布艺沙发搭配粉色落地灯，自古蓝粉出cp，两种颜色放一起简直不能更般配！&lt;/span&gt; \r\n	&lt;/p&gt;\r\n	&lt;p style=&quot;color:#3E3E3E;font-family:-apple-system-font, \'Helvetica Neue\', \'PingFang SC\', \'Hiragino Sans GB\', \'Microsoft YaHei\', sans-serif;font-size:16px;text-align:center;background-color:#FFFFFF;&quot;&gt;\