INSERT INTO `gms_article` VALUES ('162', '105', '699的家装，你确定你这样不是为了撩妹？', '&lt;span style=&quot;font-size:16px;&quot;&gt;天之蓝、梦之蓝，宽广的情怀&lt;/span&gt;面朝大海、春暖花开。\r\n&lt;p style=&quot;color:#3E3E3E;font-family:-apple-system-font, \'Helvetica Neue\', \'PingFang SC\', \'Hiragino Sans GB\', \'Microsoft YaHei\', sans-serif;font-size:16px;background-color:#FFFFFF;&quot;&gt;\r\n	&lt;br /&gt;\r\n&lt;/p&gt;\r\n&lt;br /&gt;\r\n&lt;p&gt;\r\n	今天我们要一起&lt;span style=&quot;line-height:1.5;&quot;&gt;了解&lt;/span&gt;&lt;span style=&quot;color:#5F9CEF;font-size:18px;&quot;&gt;地中海&lt;/span&gt;&lt;span style=&quot;line-height:1.5;&quot;&gt;式的家居风格&lt;/span&gt;\r\n&lt;/p&gt;\r\n&lt;img class=&quot;&quot; src=&quot;http://mmbiz.qpic.cn/mmbiz/CJJS0Pw5lwngEPtkx0cIibDS66Oibqr3V42rryT9usTNsHEHoBic076yIYQep9tfmAudW7xpbqdDeffSiatYLOsb3Q/640?wx_fmt=jpeg&amp;amp;tp=webp&amp;amp;wxfrom=5&amp;amp;wx_lazy=1&quot; style=&quot;height:auto !important;width:auto !important;&quot; /&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp;自由、浪漫、自然、休闲 具有独特的清新不修边幅的独特风格 &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;