INSERT INTO `gms_designer` VALUES ('17', '1', '郭娜', '/Uploads/1/image/2017-06-30/1498811242991.jpeg', '', '6', '13938832525', '13938832525', '设计源于细节', '');
INSERT INTO `gms_designer` VALUES ('15', '1', '杨晓', '/Uploads/1/image/2017-06-30/1498809973330.jpeg', '', '10', '15037908966', '15037908966', '分享设计之美', '陈设与空间艺术设计行业协会会员\nCIID45（洛阳）专委会会员\n中国注册高级室内建筑师\n');
INSERT INTO `gms_designer` VALUES ('16', '1', '王鹏飞', '/Uploads/1/image/2017-06-30/1498810089502.jpeg', '', '6', '18695999967', '18695999967', '设计源于对空间、生活、光线的极致领悟，没有错误的机构，只有各个细节的苛刻结合！', '');
INSERT INTO `gms_designer` VALUES ('8', '2', '吴梦斐', '/Uploads/1/image/2017-06-09/1496976652758.jpeg', '', '5', '13353988663', '13353988663', '设计源于生活，生活因设计而改变！成就空间和-谐，让设计物有所值，让细节缔造完美！', '');
INSERT INTO `gms_designer` VALUES ('9', '2', '卢卫东', '/Uploads/1/image/2017-06-09/1496976770940.jpeg', '', '5', '13140563443', 'lu13193991217', '一个愉悦的空间、一种尊贵的生活、一方独享的天地，都在这里寻求最合适的表有效规划布局，合理使用空间，以小博大，画龙点睛，创造美味家居生活！', '');
INSERT INTO `gms_designer` VALUES ('10', '3', '高冠群', '/Uploads/1/image/2017-06-20/1497929132207.jpeg', '', '12', '13663895102', 'gao', '设计源于生活，细节成就品质!', '首席设计师，从事装饰行业十多年，工装、家装，人生感悟：只有你想不到的设计，没有做不出的装修！');
INSERT INTO `gms_designer` VALUES ('11', '3', '李强', '/Uploads/1/image/2017-06-10/1497090778559.jpeg', '', '8', '13903797452', '619699740', '设计是一种感受，一种心态，一种舒适的、开心的生活方式。', '首席设计师，从事装饰行业五年，总有一个人会热衷于一个行业。人生感悟：每一个业主都是朋友，每一个设计都是在装点自家的家园。');
INSERT INTO `gms_designer` VALUES ('12', '3', '寇亚男', '/Uploads/1/image/2017-06-10/1497080060839.jpeg', '', '3', '15236299812', '15738535076', '设计就是着重于点、线、面的灵活应用，把整个环境营造出家的温馨。', '设计师，从事设计工作两年多，曾在焦作大成装饰担任设计师，人生格言：一切随心，用心感悟空间。风格：现代、北欧');
INSERT INTO `gms_designer` VALUES ('13', '3', '卢志花', '/Uploads/1/image/2017-06-10/1497080448627.jpeg', '', '3', '15225520325', '15225520325', '用有限空间，做无限设计，成就梦想之家\n', '设计师，从事设计工作两年，曾在郑州北京美创美家担任设计师，人生格言：让灵感自由释放，创造一个心灵渴望的空间。风格：现代、地中海\n');
